document.addEventListener("click", function (e) {
  if (e.target.classList.contains("dropdown-item")) {
    e.preventDefault();

    const cat = e.target.dataset.category;
    filterArticles(cat);
  }
});

function filterArticles(cat) {
  let result = "";

  articles.forEach((article) => {
    if (article.category == cat) {
    result +=
      '<div class="newscontainer"><div><div class="newsdiv"><a href="/thanasimiora/temp/template1.html?text=' +
      encodeURIComponent(article.id) +
      '"><img src="/thanasimiora/resources/images/' +
      article.pic +
      '.png" alt="article"/></a><h6 class="category"> ' +
      article.category +
      '</h6></div><h5 id="date">' +
      article.date +
      '</h5></div><a href="/thanasimiora/temp/template1.html?text=' +
      encodeURIComponent(article.id) +
      '"><h1 id="title">' +
      article.title +
      "</h1></a></div>";
    }

    
    if (newsEl) {
      newsEl.innerHTML = result;
    } else {
      const newstemplate1 =
        document.getElementById("testing");
      newstemplate1.innerHTML = result
      document.getElementById("comments-section").innerHTML ="";
    }

  });
}

